import React, { useState, useEffect } from "react";
import { addEmployee, uploadToCloudinary } from "../utils/ApiFunctions";
import RoleSelector from "../employee/RoleSelector";
import { Link } from "react-router-dom";
import axios from "axios";

const AddEmployee = () => {
  const [newEmployee, setNewEmployee] = useState({
    username: "",
    name: "", // Thêm trường name
    email: "",
    role: "",
    password: "123456", // Mật khẩu mặc định
    isActive: true,
    avatar: null,
  });

  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [imagePreview, setImagePreview] = useState("");
  const [roles, setRoles] = useState([]);

  const handleEmployeeInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmployee({ ...newEmployee, [name]: value });
  };

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setNewEmployee({ ...newEmployee, avatar: selectedImage });
    setImagePreview(URL.createObjectURL(selectedImage));
  };

//   useEffect(() => {
//     // Fetch roles from the API
//     const fetchRoles = async () => {
//       try {
//         const response = await axios.get("http://127.0.0.1:8000/roles/");
//         setRoles(response.data);
//       } catch (error) {
//         console.error("Error fetching roles:", error);
//         setErrorMessage("Error fetching roles.");
//       }
//     };

//     fetchRoles();
//   }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        const photoUrl = await uploadToCloudinary(newEmployee.avatar);
      const success = await addEmployee(
        newEmployee.username,
        newEmployee.name, // Thêm trường name
        newEmployee.email,
        newEmployee.role,
        newEmployee.password, // Mật khẩu mặc định
        newEmployee.isActive,
        photoUrl
      );
      console.log("API response:", success);
      if (success) {
        setSuccessMessage("A new employee was added successfully!");
        setNewEmployee({ username: "", name: "", email: "", role: "", password: "123456", isActive: true, avatar: "" });
        setImagePreview("");
        setErrorMessage("");
      } else {
        setErrorMessage("Error adding new employee");
      }
    } catch (error) {
      setErrorMessage(error.message);
    }
    setTimeout(() => {
      setSuccessMessage("");
      setErrorMessage("");
    }, 3000);
  };

  return (
    <>
      <section className="container mt-5 mb-5">
        <div className="row justify-content-center">
          <div className="col-md-8 col-lg-6">
            <h2 className="mt-5 mb-2">Add a New Employee</h2>
            {successMessage && (
              <div className="alert alert-success fade show">{successMessage}</div>
            )}

            {errorMessage && <div className="alert alert-danger fade show">{errorMessage}</div>}

            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="username" className="form-label">
                  Username
                </label>
                <input
                  required
                  type="text"
                  className="form-control"
                  id="username"
                  name="username"
                  value={newEmployee.username}
                  onChange={handleEmployeeInputChange}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="name" className="form-label">
                  Name
                </label>
                <input
                  required
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  value={newEmployee.name}
                  onChange={handleEmployeeInputChange}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">
                  Email
                </label>
                <input
                  required
                  type="email"
                  className="form-control"
                  id="email"
                  name="email"
                  value={newEmployee.email}
                  onChange={handleEmployeeInputChange}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="role" className="form-label">
                  Role
                </label>
                <RoleSelector
                  handleEmployeeInputChange={handleEmployeeInputChange}
                  newEmployee={newEmployee}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="avatar" className="form-label">
                  Avatar
                </label>
                <input
                  type="file"
                  className="form-control"
                  id="avatar"
                  name="avatar"
                  onChange={handleImageChange}
                />
                {imagePreview && <img src={imagePreview} alt="Avatar Preview" className="mt-3" />}
              </div>
              <div className="d-grid gap-2 d-md-flex mt-2">
                <Link to={"/existing-employees"} className="btn btn-outline-info">
                  Existing employees
                </Link>
                <button type="submit" className="btn btn-outline-primary ml-5">
                  Save Employee
                </button>
              </div>
            </form>
          </div>
        </div>
      </section>
    </>
  );
};

export default AddEmployee;
