import React, { useState, useContext } from 'react';
import { css } from '@emotion/react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { MyUserContext } from '../../configs/MyContext';
import { createReservation } from '../../configs/APIs';

const RoomCard = ({ room }) => {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [checkinDate, setCheckinDate] = useState('');
    const [numNights, setNumNights] = useState(1);
    const user = useContext(MyUserContext);

    const handleBookRoom = async () => {
        const checkoutDate = new Date(checkinDate);
        checkoutDate.setDate(checkoutDate.getDate() + numNights);
        const formattedCheckoutDate = checkoutDate.toISOString().split('T')[0]; // Format to YYYY-MM-DD

        const reservationData = {
            guest: user.username, // Assuming this is the identifier for the guest
            room: [{
                nameRoom: room.nameRoomType, // Room name or ID
                roomType: room.type, // Room type or ID
                status: 1, // Status value (check with your model)
                active: true
            }],
            bookDate: new Date().toISOString(),
            checkin: checkinDate,
            checkout: formattedCheckoutDate
        };

        try {
            await createReservation(reservationData);
            alert('Room booked successfully!');
            setModalIsOpen(false);
        } catch (error) {
            alert('Failed to book room.');
        }
    };

    const handleNumNightsChange = (e) => {
        const nights = parseInt(e.target.value, 10);
        setNumNights(nights);
    };

    return (
        <div css={styles}>
            <img src={room.image} alt={room.type} />
            <div className="room-details">
                <p>Loại phòng: {room.nameRoomType}</p>
                <p>Giá phòng: {room.price} / đêm</p>
                <p>Số lượng người: {room.quantity} tối đa</p>
                <Button variant="primary" onClick={() => setModalIsOpen(true)}>
                    Đặt phòng
                </Button>
            </div>

            {/* Modal for selecting check-in date and number of nights */}
            <Modal show={modalIsOpen} onHide={() => setModalIsOpen(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Chọn ngày check-in và số đêm</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group controlId="checkinDate">
                        <Form.Label>Ngày check-in</Form.Label>
                        <Form.Control
                            type="date"
                            value={checkinDate}
                            onChange={(e) => setCheckinDate(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="numNights">
                        <Form.Label>Số đêm</Form.Label>
                        <Form.Control
                            type="number"
                            value={numNights}
                            onChange={handleNumNightsChange}
                        />
                    </Form.Group>
                    <p>Ngày check-out dự kiến: {checkinDate && calculateCheckoutDate()}</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setModalIsOpen(false)}>
                        Đóng
                    </Button>
                    <Button variant="primary" onClick={handleBookRoom}>
                        Đặt phòng
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

const styles = css`
    background-color: #ffffff;
    border: 1px solid #e0e0e0;
    border-radius: 8px;
    padding: 16px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);

    img {
        width: 100%;
        height: 300px;
        object-fit: cover;
        border-radius: 8px;
    }

    .room-details {
        margin-top: 12px;

        p {
            margin-bottom: 4px;
            font-size: 14px;
            color: #666666;
        }
    }
`;

export default RoomCard;
