/** @jsx jsx */
import { jsx, css } from "@emotion/react";
import { useContext, useState } from "react";
import { MyUserContext } from "../../configs/MyContext";
import { updateUser } from "../../configs/APIs"; // Import updateUser function
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const Info = () => {
    const user = useContext(MyUserContext);

    const [editableUser, setEditableUser] = useState({
        name: user?.name || '',
        address: user?.Address || '',
        phone: user?.phone || '',
        dob: user?.DOB || '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditableUser((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await updateUser(editableUser);
            if (response.status === 200) {
                console.log('User information updated successfully:', response.data);
                // Update user context with new data if needed
            }
        } catch (error) {
            console.error('Failed to update user information:', error);
        }
    };

    if (!user) {
        return <p>Please log in to see your information.</p>;
    }

    return (
        <div css={styles.container}>
            <h1>Thông tin cá nhân</h1>
            <div css={styles.avatarSection}>
                <img src={user.avatar} alt={`${user.name}'s avatar`} css={styles.avatar} />
            </div>
            <Form css={styles.infoSection} onSubmit={handleSubmit}>
                <Form.Group>
                    <Form.Label><strong>Name:</strong></Form.Label>
                    <Form.Control 
                        type="text" 
                        name="name" 
                        value={editableUser.name} 
                        onChange={handleChange} 
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label><strong>Username:</strong></Form.Label>
                    <Form.Control 
                        type="text" 
                        value={user.username} 
                        readOnly 
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label><strong>Address:</strong></Form.Label>
                    <Form.Control 
                        type="text" 
                        name="address" 
                        value={editableUser.address} 
                        onChange={handleChange} 
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label><strong>Phone:</strong></Form.Label>
                    <Form.Control 
                        type="text" 
                        name="phone" 
                        value={editableUser.phone} 
                        onChange={handleChange} 
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label><strong>DOB:</strong></Form.Label>
                    <Form.Control 
                        type="date" 
                        name="dob" 
                        value={editableUser.dob} 
                        onChange={handleChange} 
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label><strong>Email:</strong></Form.Label>
                    <Form.Control 
                        type="email" 
                        value={user.email} 
                        readOnly 
                    />
                </Form.Group>
                <Button type="submit" variant="primary">Update Information</Button>
            </Form>
        </div>
    );
};

const styles = {
    container: css`
        max-width: 800px;
        margin: 20px auto;
        padding: 20px;
        padding-top: 100px; /* Adjust based on navbar height */
        background: #f9f9f9;
        border-radius: 10px;
        box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    `,
    avatarSection: css`
        display: flex;
        justify-content: center;
        margin-bottom: 20px;
    `,
    avatar: css`
        width: 150px;
        height: 150px;
        border-radius: 50%;
        object-fit: cover;
    `,
    infoSection: css`
        p {
            font-size: 18px;
            margin: 10px 0;
            strong {
                color: #333;
            }
        }
    `
};

export default Info;
