/** @jsx jsx */
import { jsx, css } from '@emotion/react';
import { useState, useContext } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { MyUserContext } from '../../configs/MyContext';
import { authAPI, endpoints } from "../../configs/APIs";

const RoomCard = ({ room }) => {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [checkinDate, setCheckinDate] = useState('');
    const [numNights, setNumNights] = useState(1);
    const user = useContext(MyUserContext);

    const handleBookRoom = async () => {
        if (!user) {
            console.error('User not logged in');
            return;
        }

        const checkoutDate = calculateCheckoutDate();
        const bookingData = {
            guest_id: user.id, // Dùng ID người dùng từ context
            room_id: room.id,
            checkin: checkinDate,
            checkout: checkoutDate,
            bookDate: new Date().toISOString().split('T')[0] // Ngày hiện tại
        };

        try {
            // Gửi yêu cầu đặt phòng qua API hiện tại
            await authAPI().post(endpoints.reservations, bookingData);
            console.log('Room booked successfully');
            setModalIsOpen(false);
        } catch (error) {
            console.error('Failed to book room:', error);
        }
    };

    const handleNumNightsChange = (e) => {
        const nights = parseInt(e.target.value, 10);
        setNumNights(nights);
    };

    const calculateCheckoutDate = () => {
        const startDate = new Date(checkinDate);
        const checkoutDate = new Date(startDate);
        checkoutDate.setDate(startDate.getDate() + numNights);
        return checkoutDate.toLocaleDateString('en-GB'); // Định dạng ngày theo yêu cầu
    };

    return (
        <div css={styles}>
            <img src={room.image} alt={room.type} />
            <div className="room-details">
                <p>Loại phòng: {room.nameRoomType}</p>
                <p>Giá phòng: {room.price} / đêm</p>
                <p>Số lượng người: {room.quantity} tối đa</p>
                <Button variant="primary" onClick={() => setModalIsOpen(true)}>
                    Đặt phòng
                </Button>
            </div>

            <Modal show={modalIsOpen} onHide={() => setModalIsOpen(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Chọn ngày check-in và số đêm</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group controlId="checkinDate">
                        <Form.Label>Ngày check-in</Form.Label>
                        <Form.Control
                            type="date"
                            value={checkinDate}
                            onChange={(e) => setCheckinDate(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="numNights">
                        <Form.Label>Số đêm</Form.Label>
                        <Form.Control
                            type="number"
                            value={numNights}
                            onChange={handleNumNightsChange}
                        />
                    </Form.Group>
                    <p>Ngày check-out dự kiến: {checkinDate && calculateCheckoutDate()}</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setModalIsOpen(false)}>
                        Đóng
                    </Button>
                    <Button variant="primary" onClick={handleBookRoom}>
                        Đặt phòng
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

const styles = css`
    background-color: #ffffff;
    border: 1px solid #e0e0e0;
    border-radius: 8px;
    padding: 16px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);

    img {
        width: 100%;
        height: 300px;
        object-fit: cover;
        border-radius: 8px;
    }

    .room-details {
        margin-top: 12px;

        p {
            margin-bottom: 4px;
            font-size: 14px;
            color: #666666;
        }
    }
`;

export default RoomCard;
