/** @jsx jsx */
import { css, jsx } from '@emotion/react';
import { useEffect, useState } from 'react';
import { authAPI, endpoints } from '../../configs/APIs';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const ReservationDetailsModal = ({ showModal, handleClose, selectedReservation, setSelectedReservation, handleUpdate, handleDelete }) => {
    const [availableRooms, setAvailableRooms] = useState([]);

    useEffect(() => {
        const fetchRooms = async () => {
            try {
                const response = await authAPI().get('http://127.0.0.1:8000/reservations/');
                // const response = await authAPI().get('/reservations/');
                setAvailableRooms(response.data);
                console.log(response.data)
            } catch (error) {
                console.error('There was an error fetching the rooms!', error);
            }
        };

        fetchRooms();
    }, []);

    return (
        <Modal show={showModal} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Chi tiết Đặt Phòng</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {selectedReservation && (
                    <Form>
                        <Form.Group controlId="formReservationId">
                            <Form.Label>ID</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedReservation.id}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formGuest">
                            <Form.Label>Khách</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedReservation.guest}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formRoom">
                            <Form.Label>Phòng</Form.Label>
                            <Form.Select
                                value={selectedReservation.room[0]?.nameRoom || ''}
                                onChange={(e) => setSelectedReservation(prev => ({
                                    ...prev,
                                    room: availableRooms.filter(room => room.nameRoom === e.target.value)
                                }))}
                            >
                                <option value="">Chọn phòng</option>
                                {availableRooms.map(room => (
                                    <option key={room.id} value={room.nameRoom}>
                                        {room.nameRoom}
                                    </option>
                                ))}
                            </Form.Select>
                        </Form.Group>
                        <Form.Group controlId="formBookDate">
                            <Form.Label>Ngày đặt</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedReservation.bookDate}
                                onChange={(e) => setSelectedReservation(prev => ({ ...prev, bookDate: e.target.value }))}
                            />
                        </Form.Group>
                        <Form.Group controlId="formCheckin">
                            <Form.Label>Nhận phòng</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedReservation.checkin}
                                onChange={(e) => setSelectedReservation(prev => ({ ...prev, checkin: e.target.value }))}
                            />
                        </Form.Group>
                        <Form.Group controlId="formCheckout">
                            <Form.Label>Trả phòng</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedReservation.checkout}
                                onChange={(e) => setSelectedReservation(prev => ({ ...prev, checkout: e.target.value }))}
                            />
                        </Form.Group>
                        <Form.Group controlId="formActive">
                            <Form.Label>Trạng thái</Form.Label>
                            <Form.Check
                                type="checkbox"
                                checked={selectedReservation.active}
                                onChange={(e) => setSelectedReservation(prev => ({ ...prev, active: e.target.checked }))}
                            />
                        </Form.Group>
                    </Form>
                )}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Đóng
                </Button>
                <Button variant="danger" onClick={handleDelete}>
                    Xóa
                </Button>
                <Button variant="primary" onClick={handleUpdate}>
                    Sửa
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

const ManageBookings = () => {
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [selectedReservation, setSelectedReservation] = useState(null);

    const handleShow = (reservation) => {
        setSelectedReservation(reservation);
        setShowModal(true);
    };

    const handleClose = () => setShowModal(false);

    const handleUpdate = async () => {
        if (!selectedReservation) return;
        // Implement the API call for updating reservation
        try {
            await authAPI().patch(`${endpoints['reservations']}${selectedReservation.id}/`, selectedReservation);
            // Refresh the reservation list after update
            const response = await authAPI().get(endpoints['reservations']);
            setReservations(response.data);
        } catch (error) {
            setError('Failed to update reservation');
        } finally {
            handleClose();
        }
    };

    const handleDelete = async () => {
        if (!selectedReservation) return;
        // Implement the API call for deleting reservation
        try {
            await authAPI().delete(`${endpoints['reservations']}${selectedReservation.id}/`);
            // Refresh the reservation list after deletion
            const response = await authAPI().get(endpoints['reservations']);
            setReservations(response.data);
        } catch (error) {
            setError('Failed to delete reservation');
        } finally {
            handleClose();
        }
    };

    useEffect(() => {
        const fetchReservations = async () => {
            try {
                const response = await authAPI().get(endpoints['reservations']);
                setReservations(response.data);
            } catch (err) {
                setError('Failed to fetch reservations');
            } finally {
                setLoading(false);
            }
        };

        fetchReservations();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div css={styles}>
            <h1>Quản lý Đặt Phòng</h1>
            <table css={tableStyles}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Khách</th>
                        <th>Phòng</th>
                        <th>Ngày đặt</th>
                        <th>Nhận phòng</th>
                        <th>Trả phòng</th>
                        <th>Trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {reservations.map(reservation => (
                        <tr key={reservation.id}>
                            <td>{reservation.id}</td>
                            <td>{reservation.guest}</td>
                            <td>{reservation.room.map(r => r.nameRoom).join(', ')}</td>
                            <td>{reservation.bookDate}</td>
                            <td>{reservation.checkin}</td>
                            <td>{reservation.checkout}</td>
                            <td>{reservation.active ? 'Active' : 'Inactive'}</td>
                            <td>
                                <Button variant="primary" onClick={() => handleShow(reservation)}>Sửa</Button>
                                <Button variant="danger" onClick={() => handleDelete(reservation)}>Xóa</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <ReservationDetailsModal
                showModal={showModal}
                handleClose={handleClose}
                selectedReservation={selectedReservation}
                setSelectedReservation={setSelectedReservation}
                handleUpdate={handleUpdate}
                handleDelete={handleDelete}
            />
        </div>
    );
};

const styles = css`
    padding-top: 120px;
    background-color: #f4f4f4;
    min-height: 100vh;

    h1 {
        color: #0000CD;
    }

    p {
        font-size: 16px;
    }
`;

const tableStyles = css`
    width: 100%;
    border-collapse: collapse;

    th, td {
        border: 1px solid #ddd;
        padding: 8px;
        text-align: left;
    }

    th {
        background-color: #0000CD;
        color: white;
    }
`;

export default ManageBookings;
