/** @jsx jsx */
import { css, jsx } from '@emotion/react';
import { useEffect, useState } from 'react';
import { authAPI } from '../../configs/APIs'; 
import { Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Bill = () => {
    const [bills, setBills] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBills = async () => {
            try {
                const response = await authAPI().get('/bill/'); // Endpoint API
                setBills(response.data); // Assume response.data is an array of bills
            } catch (err) {
                setError('Failed to fetch bills');
                console.error('Failed to fetch bills:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchBills();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    if (bills.length === 0) return <p>No bills available</p>;

    return (
        <div css={styles}>
            <h1>Danh sách Hóa đơn</h1>
            <div className="bills-list">
                {bills.map(bill => (
                    <Form.Group key={bill.id} className="bill-item">
                        <Form.Label><strong>Tổng tiền:</strong></Form.Label>
                        <Form.Control type="text" readOnly value={`${bill.totalAmount} VND`} />
                        <Form.Label><strong>Tổng tiền bằng chữ:</strong></Form.Label>
                        <Form.Control type="text" readOnly value={bill.summary} />
                    </Form.Group>
                ))}
            </div>
        </div>
    );
};

const styles = css`
    padding: 20px;
    background-color: #f9f9f9; /* Light gray background */
    min-height: 100vh;
    font-family: Arial, sans-serif;

    h1 {
        color: #0056b3; /* Dark blue color for headings */
        text-align: center;
        margin-bottom: 20px;
        font-size: 24px; /* Larger font size for headings */
    }

    .bills-list {
        max-width: 800px;
        margin: 0 auto; /* Center align the content */
    }

    .bill-item {
        padding: 15px;
        margin-bottom: 15px;
        background-color: #ffffff; /* White background for each bill */
        border-radius: 8px; /* Rounded corners for each bill */
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); /* Subtle shadow for depth */
    }

    .form-label {
        font-size: 18px;
        color: #333; /* Darker text color for better readability */
    }

    .form-control {
        font-size: 16px;
        margin-bottom: 10px;
    }
`;

export default Bill;
