/** @jsx jsx */
import { css, jsx } from '@emotion/react';
import { useEffect, useState } from 'react';
import { authAPI, endpoints } from '../../configs/APIs';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const BillDetailsModal = ({ showModal, handleClose, selectedBill, setSelectedBill, handleUpdate, handleDelete }) => {
    return (
        <Modal show={showModal} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Chi tiết Hóa Đơn</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {selectedBill && (
                    <Form>
                        <Form.Group controlId="formBillId">
                            <Form.Label>ID</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.id}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formTotalAmount">
                            <Form.Label>Tổng tiền</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.totalAmount}
                                onChange={(e) => setSelectedBill(prev => ({ ...prev, totalAmount: e.target.value }))}
                            />
                        </Form.Group>
                        <Form.Group controlId="formSummary">
                            <Form.Label>Tổng tiền bằng chữ</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.summary}
                                onChange={(e) => setSelectedBill(prev => ({ ...prev, summary: e.target.value }))}
                            />
                        </Form.Group>
                    </Form>
                )}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Đóng
                </Button>
                <Button variant="danger" onClick={handleDelete}>
                    Xóa
                </Button>
                <Button variant="primary" onClick={handleUpdate}>
                    Sửa
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

const Bill = () => {
    const [bills, setBills] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [selectedBill, setSelectedBill] = useState(null);

    const handleShow = (bill) => {
        setSelectedBill(bill);
        setShowModal(true);
    };

    const handleClose = () => setShowModal(false);

    const handleUpdate = async () => {
        if (!selectedBill) return;

        const updatedBill = { ...selectedBill };
        try {
            await authAPI().post('/bill/', (updatedBill.id), updatedBill);
            const response = await authAPI().get('/bill/');
            setBills(response.data);
        } catch (error) {
            console.error('Failed to update bill:', error);
            setError('Failed to update bill');
        } finally {
            handleClose();
        }
    };

    // const handleDelete = async () => {
    //     if (!selectedBill) return;

    //     const confirmed = window.confirm('Bạn có chắc chắn muốn xóa hóa đơn này?');
    //     if (confirmed) {
    //         try {
    //             await authAPI().delete(endpoints['delete_bill'](selectedBill.id));
    //             const response = await authAPI().get(endpoints['list_bills']);
    //             setBills(response.data);
    //         } catch (error) {
    //             setError('Failed to delete bill');
    //         } finally {
    //             handleClose();
    //         }
    //     }
    // };

    useEffect(() => {
        const fetchBills = async () => {
            try {
                const response = await authAPI().get('/bill/');
                setBills(response.data);
            } catch (err) {
                setError('Failed to fetch bills');
            } finally {
                setLoading(false);
            }
        };

        fetchBills();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    if (bills.length === 0) return <p>No bills available</p>;

    return (
        <div css={styles}>
            <h1>Danh sách Hóa Đơn</h1>
            <table css={tableStyles}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tổng tiền</th>
                        <th>Tổng tiền bằng chữ</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {bills.map(bill => (
                        <tr key={bill.id}>
                            <td>{bill.id}</td>
                            <td>{bill.totalAmount} VND</td>
                            <td>{bill.summary}</td>
                            <td>
                                <Button variant="primary" onClick={() => handleShow(bill)}>Sửa</Button>
                                {/* <Button variant="danger" onClick={() => handleDelete(bill)}>Xóa</Button> */}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <BillDetailsModal
                showModal={showModal}
                handleClose={handleClose}
                selectedBill={selectedBill}
                setSelectedBill={setSelectedBill}
                handleUpdate={handleUpdate}
                // handleDelete={handleDelete}
            />
        </div>
    );
};

const styles = css`
    padding: 120px;
    background-color: #f9f9f9;
    min-height: 100vh;

    h1 {
        color: #0056b3;
        text-align: center;
        margin-bottom: 20px;
    }
`;

const tableStyles = css`
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;

    th, td {
        border: 1px solid #ddd;
        padding: 8px;
        text-align: left;
    }

    th {
        background-color: #0000CD;
        color: white;
    }

    tr:nth-of-type(even) {
        background-color: #f2f2f2;
    }

    tr:hover {
        background-color: #ddd;
    }

    td {
        max-width: 150px;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
    }
`;

export default Bill;
