import axios from "axios";
import moment from "moment";
import cookie from "react-cookies";

export const BASE_URL = 'http://192.168.1.113:8000/';

export const formatNS = (dateString) => {
    const [year, month, day] = dateString.split('-');
    return `${day}/${month}/${year}`;
};

export const formatDate = (date) => {
    return moment(date).format(' HH:mm - DD/MM/YYYY');
};

export const endpoints = {
    'login': '/o/token/',
    'current-user': '/accounts/current-user/',
    'signup': '/accounts/',
    'roomtypes': '/roomtypes/',
    'reservations': '/reservations/',

};

// Hàm tạo instance axios với Authorization header
export const authAPI = () => {
    const token = cookie.load('token');
    return axios.create({
        baseURL: BASE_URL,
        headers: {
            'Authorization': `Bearer ${token}`
        }
    });
};

// Hàm tạo instance axios không có Authorization header
export const api = axios.create({
    baseURL: BASE_URL
});

// Hàm gửi yêu cầu đặt phòng
export const createReservation = async (reservationData) => {
    try {
        // console.log(reservationData);
        const response = await authAPI().post(endpoints.reservations, reservationData);
        return response; // Return the entire response object
    } catch (error) {
        console.error('Failed to create reservation:', error);
        throw error; 
    }
};

// Hàm cập nhật thông tin người dùng
export const updateUser = async (userData) => {
    try {
        console.log(userData);
        const response = await authAPI().put(endpoints['current-user'], userData);
        return response;
    } catch (error) {
        console.error('Failed to update user:', error);
        throw error;
    }
};

export default api;
