/** @jsx jsx */
import { css, jsx } from '@emotion/react';
import { useEffect, useState } from 'react';
import { authAPI } from '../../configs/APIs';
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import BillDetailsModal from './BillDetailsModal';

const Bill = () => {
    const [bills, setBills] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [selectedBill, setSelectedBill] = useState(null);

    const handleShow = (bill) => {
        setSelectedBill(bill);
        setShowModal(true);
    };

    const handleClose = () => setShowModal(false);

    useEffect(() => {
        const fetchBills = async () => {
            try {
                const response = await authAPI().get('/bill/');
                setBills(response.data);
            } catch (err) {
                setError('Failed to fetch bills');
            } finally {
                setLoading(false);
            }
        };

        fetchBills();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    if (bills.length === 0) return <p>No bills available</p>;

    return (
        <div css={styles}>
            <h1>Danh sách Hóa Đơn</h1>
            <table css={tableStyles}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tổng tiền</th>
                        <th>Tổng tiền bằng chữ</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {bills.map(bill => (
                        <tr key={bill.id}>
                            <td>{bill.id}</td>
                            <td>{bill.totalAmount} VND</td>
                            <td>{bill.summary}</td>
                            <td>
                                <Button variant="primary" onClick={() => handleShow(bill)}>Sửa</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <BillDetailsModal
                showModal={showModal}
                handleClose={handleClose}
                selectedBill={selectedBill}
            />
        </div>
    );
};

const styles = css`
    padding: 120px;
    background-color: #f9f9f9;
    min-height: 100vh;

    h1 {
        color: #0056b3;
        text-align: center;
        margin-bottom: 20px;
    }
`;

const tableStyles = css`
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;

    th, td {
        border: 1px solid #ddd;
        padding: 8px;
        text-align: left;
    }

    th {
        background-color: #0000CD;
        color: white;
    }

    tr:nth-of-type(even) {
        background-color: #f2f2f2;
    }

    tr:hover {
        background-color: #ddd;
    }

    td {
        max-width: 150px;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
    }
`;

export default Bill;
