/** @jsx jsx */
import { css, jsx } from '@emotion/react';
import { useEffect, useState } from 'react';
import { authAPI } from '../../configs/APIs'; 

const Bill = () => {
    const [bill, setBill] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBill = async () => {
            try {
                const response = await authAPI().get('/bill/'); // Endpoint API
                setBill(response.data);
            } catch (err) {
                setError('Failed to fetch bill information');
                console.error('Failed to fetch bill information:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchBill();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    if (!bill) return <p>No bill information available</p>;

    // Chỉ lấy thông tin có trong bill
    const { totalAmount, summary } = bill;

    return (
        <div css={styles}>
            <h1>Hóa đơn</h1>
            <div>
                <p><strong>Tổng tiền:</strong> {totalAmount} VND</p>
                <p><strong>Tổng tiền bằng chữ:</strong> {summary}</p>
            </div>
        </div>
    );
};

const styles = css`
    padding: 20px;
    background-color: #f4f4f4;
    min-height: 100vh;

    h1 {
        color: #0000CD;
        text-align: center;
        margin-bottom: 20px;
    }

    div {
        text-align: center;
    }

    p {
        font-size: 18px;
        margin: 10px 0;
    }

    strong {
        color: #0000CD;
    }
`;

export default Bill;
