import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { getRoomTypeById, updateRoomType } from "../../configs/APIs"

const EditRoom = () => {
  const [roomType, setRoomType] = useState({
    nameRoomType: "",
    price: "",
    quantity: "",
    image: ""
  });
  const [imagePreview, setImagePreview] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const { roomTypeId } = useParams();

  useEffect(() => {
    // Simulate fetching room type data
    const fetchRoomType = () => {
      setRoomType(mockRoomTypeData);
      setImagePreview(mockRoomTypeData.image);
    };
    fetchRoomType();
  }, [roomTypeId]);

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setRoomType({ ...roomType, image: selectedImage });
    setImagePreview(URL.createObjectURL(selectedImage));
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setRoomType({ ...roomType, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await mockUpdateRoomType(roomTypeId, roomType);
      if (response.status === 0) {
        setSuccessMessage("Room type updated successfully!");
        setErrorMessage("");
      } else {
        setErrorMessage("Error updating room type");
      }
    } catch (error) {
      console.error(error);
      setErrorMessage(error.message);
    }
    setTimeout(() => {
      setSuccessMessage("");
      setErrorMessage("");
    }, 3000);
  };

  return (
    <div className="container mt-5 mb-5">
      <h3 className="text-center mb-5 mt-5">Edit Room Type</h3>
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6">
          {successMessage && (
            <div className="alert alert-success" role="alert">
              {successMessage}
            </div>
          )}
          {errorMessage && (
            <div className="alert alert-danger" role="alert">
              {errorMessage}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="nameRoomType" className="form-label hotel-color">
                Room Type Name
              </label>
              <input
                type="text"
                className="form-control"
                id="nameRoomType"
                name="nameRoomType"
                value={roomType.nameRoomType}
                onChange={handleInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="price" className="form-label">
                Price
              </label>
              <input
                type="text"
                className="form-control"
                id="price"
                name="price"
                value={roomType.price}
                onChange={handleInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="quantity" className="form-label">
                Quantity
              </label>
              <input
                type="text"
                className="form-control"
                id="quantity"
                name="quantity"
                value={roomType.quantity}
                onChange={handleInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="image" className="form-label">
                Room Type Image
              </label>
              <input
                type="file"
                className="form-control"
                id="image"
                name="image"
                onChange={handleImageChange}
              />
            </div>
            {imagePreview && (
              <img
                src={imagePreview}
                alt="Room Type preview"
                style={{ maxWidth: "400px", maxHeight: "400px" }}
                className="mt-3"
              />
            )}
            <div className="d-grid gap-2 d-md-flex mt-2">
              <Link to={"/existing-roomtypes"} className="btn btn-outline-info ml-5">
                Back
              </Link>
              <button type="submit" className="btn btn-outline-warning">
                Edit Room Type
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditRoom;
