import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Form, Button, Container, Row } from 'react-bootstrap';
import APIs, { endpoints, authAPI } from "../../configs/APIs";
import cookie from "react-cookies";
import { MyDispatchContext } from '../../configs/MyContext';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useContext(MyDispatchContext);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const navigate = useNavigate(); // Initialize the navigate function

    const handleLoginError = (errorStatus) => {
        switch (errorStatus) {
            case 400:
                setError("Sai tên đăng nhập hoặc mật khẩu");
                break;
            default:
                setError("Đăng nhập không thành công");
                break;
        }
    };

    const login = async () => {
        setError("");
        setLoading(true);

        try {
            let res = await APIs.post(endpoints['login'], {
                'username': username,
                'password': password,
                'client_id': "6RK7vSiRjaZDINae5ru1BzfPx7u7vmqJ3vmBK2sV",
                'client_secret': "DtsNOQXIqsMRpPXjhJlS8WMWhbL9XiXVETxLaCAkTk5MDNLdUF5gysLaaHN6XE0KTVQTUXdbeMs6rXPhVP6KvtW0BOfEN0TZfgJb4lnRO5aHEZQbkoTVkqnt3ue9fubM",
                'grant_type': "password",
            }, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            console.log̣̣̣̣̣̣̣()
            if (res.status === 200) {
                cookie.save("token", res.data.access_token);
                let userdata = await authAPI().get(endpoints['current-user']);
                cookie.save('user', userdata.data);

                dispatch({
                    type: "login",
                    payload: userdata.data
                });

                navigate('/'); // Redirect to the home page
            } else {       
                handleLoginError(res.status);
                console.error("Đăng nhập không thành công:", res);
            }
        } catch (ex) {
            console.error("Lỗi tại màn hình đăng nhập:", ex);
            setError("Sai tên hoặc mật khẩu, vui lòng thử lại.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container
            fluid
            className="d-flex justify-content-center align-items-center"
            style={{
                height: '100vh',
                backgroundImage: 'url("https://cache.marriott.com/marriottassets/marriott/BOMSA/bomsa-exterior-0023-hor-feat.jpg")',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat',
            }}
        >
            <Row className="justify-content-md-center" style={{ width: '100%' }}>
                <div className="card p-4 shadow bg-white rounded" style={{ maxWidth: '500px', width: '100%' }}>
                    <Form>
                        <h1 className="text-center mb-4">Đăng nhập</h1>
                        {error && <div className="alert alert-danger" role="alert">{error}</div>}
                        <Form.Group controlId="formBasicEmail" style={{ marginBottom: '20px' }}>
                            <Form.Control
                                type="text"
                                placeholder="Nhập tên đăng nhập"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword" style={{ marginBottom: '20px' }}>
                            <Form.Control
                                type="password"
                                placeholder="Nhập mật khẩu"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Form.Group controlId="formBasicCheckbox" className="d-flex justify-content-between align-items-center" style={{ marginBottom: '20px' }}>
                            <Form.Check type="checkbox" label="Nhớ tài khoản" />
                            <Button variant="link" onClick={() => { /* Xử lý quên mật khẩu */ }}>
                                Quên mật khẩu
                            </Button>
                        </Form.Group>

                        <Button variant="primary" type="button" className="w-100" onClick={login} disabled={loading}>
                            {loading ? 'Đang đăng nhập...' : 'Đăng nhập'}
                        </Button>

                        <div className="text-center mt-3">
                            <Link to="/signup" style={{ color: '#6c757d', textDecoration: 'underline', cursor: 'pointer' }}>
                                Đăng ký
                            </Link>
                        </div>
                    </Form>
                </div>
            </Row>
        </Container>
    );
};

export default Login;
