/** @jsx jsx */
import { css, jsx } from '@emotion/react';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import { authAPI } from '../../configs/APIs'; // Ensure this is your API config

const BillDetailsModal = ({ showModal, handleClose, selectedBill }) => {
    const [totalAmount, setTotalAmount] = useState(calculateTotalAmount(selectedBill));
    const [error, setError] = useState(null);

    // Function to calculate total amount based on selectedBill
    function calculateTotalAmount(bill) {
        const roomPrice = bill?.reservation?.room?.[0]?.roomType?.price.replace(/[^0-9]/g, '') || 0;
        const services = bill?.reservation?.services || [];
        
        const servicesTotal = services.reduce((total, service) => {
            const servicePrice = parseFloat(service.price);
            return total + (servicePrice * service.quantity);
        }, 0);

        return parseFloat(roomPrice) + servicesTotal;
    }

    const handleTotalAmountChange = (e) => {
        setTotalAmount(e.target.value);
    };

    const handleSave = async () => {
        try {
            await authAPI().post('/bill/', { totalAmount });
            handleClose();
        } catch (err) {
            setError('Failed to update total amount');
        }
    };

    return (
        <Modal show={showModal} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Chi tiết Hóa Đơn</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {selectedBill && (
                    <Form>
                        <Form.Group controlId="formGuestName">
                            <Form.Label>Tên Khách</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.guest.name}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formRoomName">
                            <Form.Label>Tên Phòng</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.reservation.room[0].nameRoom}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formRoomPrice">
                            <Form.Label>Giá Phòng</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.reservation.room[0].roomType.price}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formCheckinDate">
                            <Form.Label>Ngày Check-in</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.reservation.checkin}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formCheckoutDate">
                            <Form.Label>Ngày Check-out</Form.Label>
                            <Form.Control
                                type="text"
                                value={selectedBill.reservation.checkout}
                                readOnly
                            />
                        </Form.Group>
                        <Form.Group controlId="formServices">
                            <Form.Label>Dịch vụ</Form.Label>
                            {selectedBill.reservation.services.map((service, index) => (
                                <Form.Text key={index}>
                                    {service.price} x {service.quantity}
                                </Form.Text>
                            ))}
                        </Form.Group>
                        <Form.Group controlId="formTotalAmount">
                            <Form.Label>Tổng tiền</Form.Label>
                            <Form.Control
                                type="number"
                                value={totalAmount}
                                onChange={handleTotalAmountChange}
                            />
                        </Form.Group>
                        {error && <p css={errorStyles}>{error}</p>}
                    </Form>
                )}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Đóng
                </Button>
                <Button variant="primary" onClick={handleSave}>
                    Lưu
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

const errorStyles = css`
    color: red;
`;

export default BillDetailsModal;
