/** @jsx jsx */
import { css, jsx } from '@emotion/react';
import { useEffect, useState } from 'react';
import { authAPI } from '../../configs/APIs';
import { Table } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Bill = ({ reservationId }) => {
    const [bill, setBill] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBill = async () => {
            try {
                const response = await authAPI().get(`bill`);
                setBill(response.data);
            } catch (err) {
                setError('Failed to fetch bill information');
                console.error('Failed to fetch bill information:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchBill();
    }, [reservationId]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    if (!bill) return <p>No bill information available</p>;

    return (
        <div css={styles}>
            <h1>Hóa đơn</h1>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Tên người đặt phòng</th>
                        <th>Tổng tiền</th>
                        <th>Tổng tiền bằng chữ</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{bill.reservation.guest.name}</td>
                        <td>{bill.totalAmount} VND</td>
                        <td>{bill.summary}</td>
                    </tr>
                </tbody>
            </Table>
        </div>
    );
};

const styles = css`
    padding: 20px;
    background-color: #f4f4f4;
    min-height: 100vh;

    h1 {
        color: #0000CD;
        text-align: center;
        margin-bottom: 20px;
    }

    table {
        margin: 0 auto;
        width: 80%;
        background-color: white;
    }

    th {
        background-color: #0000CD;
        color: white;
        text-align: center;
    }

    td {
        text-align: center;
    }
`;

export default Bill;
