/** @jsx jsx */
import { jsx, css } from "@emotion/react";
import React, {useState} from "react"
import { Link, useNavigate } from "react-router-dom"

const Admin = () => {
	const [selected, setSelected] = useState("");
	const navigate = useNavigate();

	const handleClick = (path) => {
		setSelected(path);
		navigate(path);
	};

	return (
		<section className="container mt-5">
			<h2>Welcome to Admin Panel</h2>
			<hr />
			<div className="btn-group">
				<Link to={"/existing-rooms"} className="btn">Manage Rooms</Link>
				<Link to={"/existing-roomTypes"} className="btn">Manage RoomTypes</Link>
				{/* <Link to={"/existing-bookings"} className="btn">Manage Bookings</Link> */}
				<Link to={"/existing-employees"} className="btn">Manage Employee</Link>
			</div>
		</section>
	);

}

const containerStyle = css`
  max-width: 1000px;
  margin: 9rem auto;
  padding: 2rem;
  background-color: #f9f9f9;
  border-radius: 1rem;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const titleStyle = css`
  font-size: 2rem;
  color: #333;
  margin-bottom: 1.5rem;
`;

const btnGroupStyle = css`
  display: flex;
  justify-content: center;
  gap: 1rem;
  flex-wrap: wrap;
`;

const btnStyle = (isActive) => css`
  position: relative;
  padding: 0.75rem 1.5rem;
  background-color: ${isActive ? "#e8e8e8" : "#70c489"};
  color: ${isActive ? "#1d1d29" : "#fff"};
  border: none;
  border-radius: 0.5rem;
  font-size: 1rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.15s ease-in-out, color 0.15s ease-in-out;

  &:hover {
    background-color: ${isActive ? "#d0d0d0" : "#5aaf73"};
    color: ${isActive ? "#333" : "#fff"};
  }
`;
const buttonDividerStyle = css`
  margin: 2rem auto;
  border: none;
  height: 3px;
  background-color: #ddd;
  width: 100%; 
`;

export default Admin
